package patterns;

public class manju {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     String str="manju";
     int n=str.length();
     for(int i=0;i<n;i++){
    	 for(int j=0;j<n;j++){
    		 System.out.print(str.charAt(j)+" ");
    	 }
    	 System.out.println();
     }
	}

}
